int
rpkiRtrPrefixOriginTable_container_load(netsnmp_container * container)
{
    DEBUGMSGTL(("verbose:rpkiRtrPrefixOriginTable:rpkiRtrPrefixOriginTable_container_load", "called\n"));

    /* Do not load anything, should not currently be called. Loading
     * is done from rtrlib prefix callback. */
    return MFD_SUCCESS;

    if (bgpsec_bc_rtr_config == NULL) {
      DEBUGMSGTL(("verbose:rpkiRtrPrefixOriginTable:rpkiRtrPre fixOriginTable_container_load", 
		"bgpsec_bc_rtr_config is NULL\n"));
      return MFD_RESOURCE_UNAVAILABLE;
    }
    else {
      u_int rc = container->flags;
      container->flags =
	( CONTAINER_KEY_ALLOW_DUPLICATES | CONTAINER_KEY_UNSORTED );
      
      rtr_mgr_for_each_ipv4_record(bgpsec_bc_rtr_config,
				   &rpkiRtrPrefixOriginTable_load_ip_records,
				   (void *)container);

      rtr_mgr_for_each_ipv6_record(bgpsec_bc_rtr_config,
				   &rpkiRtrPrefixOriginTable_load_ip_records,
				   (void *)container);

      container->flags = rc;
    }
    
    DEBUGMSGT(("verbose:rpkiRtrPrefixOriginTable:rpkiRtrPrefixOriginTable_container_load", " success\n"));

    return MFD_SUCCESS;
}                               /* rpkiRtrPrefixOriginTable_container_load */


